document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".button-menu").forEach(e=>{e.addEventListener("click",n=>{n.preventDefault(),document.querySelector(".wrapper-menu").classList.toggle("hidden")})}),document.body.addEventListener("click",e=>{!e.target.classList.contains("button-dropdown")&&!e.target.closest(".button-dropdown")&&document.querySelectorAll(".button-dropdown").forEach(n=>{n.nextElementSibling.classList.contains("hidden")||n.nextElementSibling.classList.add("hidden")})}),document.querySelectorAll(".button-dropdown").forEach(e=>{e.addEventListener("click",()=>{e.nextElementSibling.classList.toggle("hidden")})}),document.querySelectorAll(".search-toggle").forEach(e=>{e.addEventListener("click",n=>{n.preventDefault();let t=document.querySelector(".wrapper-search").classList;t.remove("hidden"),t.add("flex"),t.contains("animate__slideInDown")?(t.remove("animate__slideInDown"),t.add("animate__slideOutUp")):(t.remove("animate__slideOutUp"),t.add("animate__slideInDown"))})})});
